import React, { useState } from 'react';
import { equipmentData as initialData } from './data/equipments';
import CSVImporter from './components/CSVImporter';

function App() {
  const [equipmentData, setEquipmentData] = useState(initialData);
  const [currentPage, setCurrentPage] = useState('dashboard');
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedEquipment, setSelectedEquipment] = useState(null);
  const [showImporter, setShowImporter] = useState(false);

  // Fonction appelÃ©e quand un CSV est importÃ©
  const handleDataImported = (newData) => {
    setEquipmentData(newData);
    setShowImporter(false);
    alert(`${newData.length} Ã©quipements importÃ©s avec succÃ¨s !`);
  };

  // Calcul des statistiques globales
  const stats = {
    total: equipmentData.length,
    enLocation: equipmentData.filter(eq => eq.disponibilite === 'En Location').length,
    surParc: equipmentData.filter(eq => eq.disponibilite === 'Sur Parc').length,
    enMaintenance: equipmentData.filter(eq => eq.disponibilite === 'En Maintenance').length,
    enOffre: equipmentData.filter(eq => eq.disponibilite === 'En Offre de Prix').length
  };

  // Filtrage selon la page actuelle
  const getFilteredData = () => {
    let filtered = equipmentData;
    
    switch (currentPage) {
      case 'en-location':
        filtered = equipmentData.filter(eq => eq.disponibilite === 'En Location');
        break;
      case 'en-offre':
        filtered = equipmentData.filter(eq => eq.disponibilite === 'En Offre de Prix');
        break;
      case 'maintenance':
        filtered = equipmentData.filter(eq => eq.disponibilite === 'En Maintenance');
        break;
      default:
        filtered = equipmentData;
    }

    if (searchTerm) {
      filtered = filtered.filter(equipment =>
        equipment.designation.toLowerCase().includes(searchTerm.toLowerCase()) ||
        equipment.numeroSerie.toLowerCase().includes(searchTerm.toLowerCase()) ||
        (equipment.client && equipment.client.toLowerCase().includes(searchTerm.toLowerCase()))
      );
    }

    return filtered;
  };

  // Navigation latÃ©rale
  const Sidebar = () => (
    <div style={{
      width: '250px',
      backgroundColor: '#1f2937',
      color: 'white',
      padding: '20px',
      height: '100vh',
      position: 'fixed',
      left: 0,
      top: 0
    }}>
      <h2 style={{ marginBottom: '30px', fontSize: '20px' }}>MagiLoc</h2>
      
      <nav>
        <button
          onClick={() => setCurrentPage('dashboard')}
          style={{
            width: '100%',
            padding: '12px 16px',
            backgroundColor: currentPage === 'dashboard' ? '#3b82f6' : 'transparent',
            color: 'white',
            border: 'none',
            borderRadius: '6px',
            textAlign: 'left',
            cursor: 'pointer',
            marginBottom: '8px',
            fontSize: '14px'
          }}
        >
          ğŸ“Š Tableau de bord
        </button>
        
        <button
          onClick={() => setCurrentPage('parc-loc')}
          style={{
            width: '100%',
            padding: '12px 16px',
            backgroundColor: currentPage === 'parc-loc' ? '#3b82f6' : 'transparent',
            color: 'white',
            border: 'none',
            borderRadius: '6px',
            textAlign: 'left',
            cursor: 'pointer',
            marginBottom: '8px',
            fontSize: '14px'
          }}
        >
          ğŸ­ Parc LOC
        </button>
        
        <button
          onClick={() => setCurrentPage('en-location')}
          style={{
            width: '100%',
            padding: '12px 16px',
            backgroundColor: currentPage === 'en-location' ? '#3b82f6' : 'transparent',
            color: 'white',
            border: 'none',
            borderRadius: '6px',
            textAlign: 'left',
            cursor: 'pointer',
            marginBottom: '8px',
            fontSize: '14px'
          }}
        >
          ğŸšš En Location ({stats.enLocation})
        </button>
        
        <button
          onClick={() => setCurrentPage('planning')}
          style={{
            width: '100%',
            padding: '12px 16px',
            backgroundColor: currentPage === 'planning' ? '#3b82f6' : 'transparent',
            color: 'white',
            border: 'none',
            borderRadius: '6px',
            textAlign: 'left',
            cursor: 'pointer',
            marginBottom: '8px',
            fontSize: '14px'
          }}
        >
          ğŸ“… Planning
        </button>
        
        <button
          onClick={() => setCurrentPage('en-offre')}
          style={{
            width: '100%',
            padding: '12px 16px',
            backgroundColor: currentPage === 'en-offre' ? '#3b82f6' : 'transparent',
            color: 'white',
            border: 'none',
            borderRadius: '6px',
            textAlign: 'left',
            cursor: 'pointer',
            marginBottom: '8px',
            fontSize: '14px'
          }}
        >
          ğŸ’° Offres de prix ({stats.enOffre})
        </button>
        
        <button
          onClick={() => setCurrentPage('maintenance')}
          style={{
            width: '100%',
            padding: '12px 16px',
            backgroundColor: currentPage === 'maintenance' ? '#3b82f6' : 'transparent',
            color: 'white',
            border: 'none',
            borderRadius: '6px',
            textAlign: 'left',
            cursor: 'pointer',
            marginBottom: '8px',
            fontSize: '14px'
          }}
        >
          ğŸ”§ Maintenance ({stats.enMaintenance})
        </button>
      </nav>

      <div style={{ position: 'absolute', bottom: '20px', width: '210px' }}>
        <button
          onClick={() => setShowImporter(!showImporter)}
          style={{
            width: '100%',
            padding: '10px',
            backgroundColor: '#059669',
            color: 'white',
            border: 'none',
            borderRadius: '6px',
            cursor: 'pointer',
            fontSize: '14px'
          }}
        >
          ğŸ“ Importer CSV
        </button>
      </div>
    </div>
  );

  // Tableau de bord principal
  const Dashboard = () => (
    <div>
      <h1 style={{ marginBottom: '30px' }}>Tableau de bord - Parc Location COUERON</h1>
      
      {/* Statistiques principales */}
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '20px', marginBottom: '30px' }}>
        <div style={{ padding: '25px', backgroundColor: 'white', borderRadius: '12px', boxShadow: '0 4px 6px rgba(0,0,0,0.1)', textAlign: 'center' }}>
          <div style={{ fontSize: '36px', fontWeight: 'bold', color: '#374151' }}>{stats.total}</div>
          <div style={{ color: '#6b7280', marginTop: '8px' }}>Total Ã‰quipements</div>
        </div>
        
        <div style={{ padding: '25px', backgroundColor: 'white', borderRadius: '12px', boxShadow: '0 4px 6px rgba(0,0,0,0.1)', textAlign: 'center' }}>
          <div style={{ fontSize: '36px', fontWeight: 'bold', color: '#2563eb' }}>{stats.enLocation}</div>
          <div style={{ color: '#6b7280', marginTop: '8px' }}>En Location</div>
          <div style={{ color: '#2563eb', fontSize: '14px' }}>{((stats.enLocation/stats.total)*100).toFixed(1)}%</div>
        </div>
        
        <div style={{ padding: '25px', backgroundColor: 'white', borderRadius: '12px', boxShadow: '0 4px 6px rgba(0,0,0,0.1)', textAlign: 'center' }}>
          <div style={{ fontSize: '36px', fontWeight: 'bold', color: '#16a34a' }}>{stats.surParc}</div>
          <div style={{ color: '#6b7280', marginTop: '8px' }}>Disponibles</div>
          <div style={{ color: '#16a34a', fontSize: '14px' }}>{((stats.surParc/stats.total)*100).toFixed(1)}%</div>
        </div>
        
        <div style={{ padding: '25px', backgroundColor: 'white', borderRadius: '12px', boxShadow: '0 4px 6px rgba(0,0,0,0.1)', textAlign: 'center' }}>
          <div style={{ fontSize: '36px', fontWeight: 'bold', color: '#dc2626' }}>{stats.enMaintenance}</div>
          <div style={{ color: '#6b7280', marginTop: '8px' }}>En Maintenance</div>
          <div style={{ color: '#dc2626', fontSize: '14px' }}>{((stats.enMaintenance/stats.total)*100).toFixed(1)}%</div>
        </div>
        
        <div style={{ padding: '25px', backgroundColor: 'white', borderRadius: '12px', boxShadow: '0 4px 6px rgba(0,0,0,0.1)', textAlign: 'center' }}>
          <div style={{ fontSize: '36px', fontWeight: 'bold', color: '#f59e0b' }}>{stats.enOffre}</div>
          <div style={{ color: '#6b7280', marginTop: '8px' }}>En Offre</div>
          <div style={{ color: '#f59e0b', fontSize: '14px' }}>{((stats.enOffre/stats.total)*100).toFixed(1)}%</div>
        </div>
      </div>

      {/* Alertes et actions rapides */}
      <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '20px' }}>
        <div style={{ padding: '20px', backgroundColor: 'white', borderRadius: '12px', boxShadow: '0 4px 6px rgba(0,0,0,0.1)' }}>
          <h3 style={{ marginBottom: '15px', color: '#dc2626' }}>âš ï¸ Alertes</h3>
          <div style={{ marginBottom: '10px' }}>
            <span style={{ color: '#dc2626', fontWeight: 'bold' }}>Retards de retour: </span>
            {equipmentData.filter(eq => eq.disponibilite === 'En Location' && eq.finLocationTheorique).length}
          </div>
          <div>
            <span style={{ color: '#dc2626', fontWeight: 'bold' }}>VGP Ã  prÃ©voir: </span>
            {equipmentData.filter(eq => eq.prochainVGP).length}
          </div>
        </div>
        
        <div style={{ padding: '20px', backgroundColor: 'white', borderRadius: '12px', boxShadow: '0 4px 6px rgba(0,0,0,0.1)' }}>
          <h3 style={{ marginBottom: '15px', color: '#374151' }}>ğŸš€ Actions rapides</h3>
          <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
            <button
              onClick={() => setCurrentPage('en-location')}
              style={{
                padding: '10px',
                backgroundColor: '#2563eb',
                color: 'white',
                border: 'none',
                borderRadius: '6px',
                cursor: 'pointer'
              }}
            >
              Voir les locations en cours
            </button>
            <button
              onClick={() => setCurrentPage('maintenance')}
              style={{
                padding: '10px',
                backgroundColor: '#dc2626',
                color: 'white',
                border: 'none',
                borderRadius: '6px',
                cursor: 'pointer'
              }}
            >
              GÃ©rer la maintenance
            </button>
          </div>
        </div>
      </div>
    </div>
  );

  // Fonction pour obtenir le titre de la page
  const getPageTitle = () => {
    switch (currentPage) {
      case 'dashboard': return 'Tableau de bord';
      case 'parc-loc': return 'Parc Location - Tous les Ã©quipements';
      case 'en-location': return 'Ã‰quipements en location';
      case 'planning': return 'Planning des locations';
      case 'en-offre': return 'Offres de prix en cours';
      case 'maintenance': return 'Ã‰quipements en maintenance';
      default: return 'MagiLoc';
    }
  };

  // Vue liste (pour toutes les pages sauf dashboard et planning)
  const ListView = () => {
    const filteredData = getFilteredData();
    
    return (
      <div>
        <h1 style={{ marginBottom: '20px' }}>{getPageTitle()}</h1>
        
        {/* Barre de recherche */}
        <div style={{ marginBottom: '20px' }}>
          <input
            type="text"
            placeholder="Rechercher par dÃ©signation, nÂ° sÃ©rie ou client..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            style={{
              width: '100%',
              padding: '12px',
              fontSize: '16px',
              border: '1px solid #d1d5db',
              borderRadius: '8px'
            }}
          />
        </div>

        {/* Tableau */}
        <div style={{ backgroundColor: 'white', borderRadius: '12px', overflow: 'hidden', boxShadow: '0 4px 6px rgba(0,0,0,0.1)' }}>
          <div style={{ overflowX: 'auto' }}>
            <table style={{ width: '100%', borderCollapse: 'collapse' }}>
              <thead style={{ backgroundColor: '#f9fafb' }}>
                <tr>
                  <th style={{ padding: '16px', textAlign: 'left', borderBottom: '1px solid #e5e7eb', fontWeight: '600' }}>Ã‰quipement</th>
                  <th style={{ padding: '16px', textAlign: 'left', borderBottom: '1px solid #e5e7eb', fontWeight: '600' }}>NÂ° SÃ©rie</th>
                  <th style={{ padding: '16px', textAlign: 'left', borderBottom: '1px solid #e5e7eb', fontWeight: '600' }}>Statut</th>
                  <th style={{ padding: '16px', textAlign: 'left', borderBottom: '1px solid #e5e7eb', fontWeight: '600' }}>Client</th>
                  <th style={{ padding: '16px', textAlign: 'left', borderBottom: '1px solid #e5e7eb', fontWeight: '600' }}>Dates</th>
                  <th style={{ padding: '16px', textAlign: 'left', borderBottom: '1px solid #e5e7eb', fontWeight: '600' }}>Actions</th>
                </tr>
              </thead>
              <tbody>
                {filteredData.map((equipment) => (
                  <tr key={equipment.id} style={{ borderBottom: '1px solid #e5e7eb' }}>
                    <td style={{ padding: '16px' }}>
                      <div>
                        <div style={{ fontWeight: '600', color: '#111827' }}>
                          {equipment.designation} {equipment.cmu}
                        </div>
                        <div style={{ color: '#6b7280', fontSize: '14px' }}>
                          {equipment.marque} {equipment.modele}
                        </div>
                      </div>
                    </td>
                    <td style={{ padding: '16px', fontFamily: 'monospace' }}>{equipment.numeroSerie}</td>
                    <td style={{ padding: '16px' }}>
                      <span style={{
                        padding: '6px 12px',
                        borderRadius: '20px',
                        fontSize: '12px',
                        fontWeight: '600',
                        backgroundColor: equipment.disponibilite === 'Sur Parc' ? '#dcfce7' : 
                                       equipment.disponibilite === 'En Location' ? '#dbeafe' : 
                                       equipment.disponibilite === 'En Maintenance' ? '#fecaca' : '#fef3c7',
                        color: equipment.disponibilite === 'Sur Parc' ? '#166534' : 
                               equipment.disponibilite === 'En Location' ? '#1e40af' : 
                               equipment.disponibilite === 'En Maintenance' ? '#991b1b' : '#92400e'
                      }}>
                        {equipment.disponibilite}
                      </span>
                    </td>
                    <td style={{ padding: '16px' }}>{equipment.client || '-'}</td>
                    <td style={{ padding: '16px' }}>
                      {equipment.debutLocation && (
                        <div style={{ fontSize: '14px' }}>
                          <div>DÃ©but: {equipment.debutLocation}</div>
                          {equipment.finLocationTheorique && (
                            <div>Fin: {equipment.finLocationTheorique}</div>
                          )}
                        </div>
                      )}
                    </td>
                    <td style={{ padding: '16px' }}>
                      <button
                        onClick={() => setSelectedEquipment(equipment)}
                        style={{
                          padding: '8px 16px',
                          backgroundColor: '#3b82f6',
                          color: 'white',
                          border: 'none',
                          borderRadius: '6px',
                          cursor: 'pointer',
                          fontSize: '14px'
                        }}
                      >
                        DÃ©tails
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>

        {filteredData.length === 0 && (
          <div style={{ textAlign: 'center', padding: '60px', color: '#6b7280' }}>
            Aucun Ã©quipement trouvÃ©.
          </div>
        )}
      </div>
    );
  };

  // Planning (version basique pour commencer)
  const Planning = () => (
    <div>
      <h1 style={{ marginBottom: '20px' }}>Planning des locations</h1>
      <div style={{ backgroundColor: 'white', padding: '40px', borderRadius: '12px', boxShadow: '0 4px 6px rgba(0,0,0,0.1)', textAlign: 'center' }}>
        <h3>Planning interactif</h3>
        <p style={{ color: '#6b7280', marginTop: '10px' }}>Cette fonctionnalitÃ© sera dÃ©veloppÃ©e dans les prochaines Ã©tapes</p>
        <div style={{ marginTop: '20px' }}>
          <button
            onClick={() => setCurrentPage('en-location')}
            style={{
              padding: '10px 20px',
              backgroundColor: '#2563eb',
              color: 'white',
              border: 'none',
              borderRadius: '6px',
              cursor: 'pointer'
            }}
          >
            Voir les locations en cours
          </button>
        </div>
      </div>
    </div>
  );

  // Contenu principal selon la page
  const renderMainContent = () => {
    if (selectedEquipment) {
      return (
        <div>
          <button
            onClick={() => setSelectedEquipment(null)}
            style={{
              padding: '10px 20px',
              backgroundColor: '#6b7280',
              color: 'white',
              border: 'none',
              borderRadius: '6px',
              cursor: 'pointer',
              marginBottom: '20px'
            }}
          >
            â† Retour
          </button>
          {/* Vue dÃ©taillÃ©e Ã©quipement (code existant) */}
          <div style={{ backgroundColor: 'white', borderRadius: '12px', padding: '30px', boxShadow: '0 4px 6px rgba(0,0,0,0.1)' }}>
            <h2>{selectedEquipment.designation} {selectedEquipment.cmu}</h2>
            {/* ... reste du code de vue dÃ©taillÃ©e ... */}
          </div>
        </div>
      );
    }

    switch (currentPage) {
      case 'dashboard':
        return <Dashboard />;
      case 'planning':
        return <Planning />;
      default:
        return <ListView />;
    }
  };

  return (
    <div style={{ display: 'flex', backgroundColor: '#f3f4f6', minHeight: '100vh' }}>
      <Sidebar />
      
      <div style={{ marginLeft: '250px', padding: '30px', width: 'calc(100% - 250px)' }}>
        {showImporter && (
          <div style={{ marginBottom: '20px' }}>
            <CSVImporter onDataImported={handleDataImported} />
          </div>
        )}
        
        {renderMainContent()}
      </div>
    </div>
  );
}

export default App;
